import axios from "axios"

export default class PostService{
	static async getAll(limit=10, page=1){
		const resp = await axios.get('https://jsonplaceholder.typicode.com/posts',{
			params:{
				_limit:limit,
				_page
			}
		})
		return resp
	}
}
