import React,{useState, useMemo} from 'react';
import AddForm from './Components/AddForm/AddForm';
import PostList from './Components/PostList/PostList';
import './styles/App.css';
import PostFilter from './Components/PostFilter/PostFilter';
import Modal from './Components/Modal/Modal';
import CustomButton from './Components/UI/Button/CustomButton';

function App() {

	const [posts,setPosts]=useState([
	{id:1,title:"aa-1",body:"3escription"},
	{id:2,title:"vv-2",body:"6escription"},
	{id:3,title:"cc-3",body:"1escription"}
	])
	const [filter, setFilter]=useState({sort:'', query:''})
	const [modal, setModal]=useState(false)



	const sortedAndSearchedPosts=useMemo(()=>{
		console.log('sortedAndSearchedPosts')
		return sortedPosts.filter(post=>post.title.toLowerCase().includes(filter.query.toLowerCase()))
	},[filter.query, sortedPosts])

	const createPost=(newPost)=>{
		setPosts([...posts,newPost])
		setModal(false)
	}
	const removePost=(post)=>{
		setPosts(posts.filter(p=>p.id!==post.id))
	}
	return(
		<div className='App'>
			<Modal 
				visible={modal}
				setVisible={setModal}>
				<AddForm create={createPost}/>
			</Modal>
			<div className='controls'>
				<CustomButton style={{margin:"20px 0 10px 0",background:"orange"}} onClick={()=>setModal(true)}>
				Создать пост
			</CustomButton>

			<PostFilter filter={filter} setFilter={setFilter}/>

			<PostList title={"Список постов про JS"} posts={sortedAndSearchedPosts} remove={removePost}/>
			</div>


		</div>

	)
}

export default App;
