import React,{useState} from 'react';
import CustomInput from './../Input/CustomInput';
import CustomButton from './../Button/CustomButton';
import style from './addformStyle.module.css'
const AddForm = ({posts,add}) => {
	const [post,setTitle]=useState({id:''})
	function AddPost(e) {
		e.preventDefault()
		if(title!=="" && body!==""){
			add([...posts,{id:Date.now(),title:title,body:body}])
			setTitle('')
			setBody('')
		}
		
	}
	return (
		<form className={style.addForm}>
			<CustomInput 
				type="text"
				placeholder='Title'
				onChange={e=>setTitle(e.target.value)}
				value={title}
			/>
			<CustomInput 
				type="text"
				placeholder='Body'
				onChange={e=>setBody(e.target.value)}
				value={body}
			/>
			<CustomButton style={{backgroundColor:"lime"}} onClick={AddPost}>Добавить</CustomButton>
		</form>
	);
}

export default AddForm;
