import React from 'react';
import style from './modal.module.css';
const Modal = ({children, visible, setVisible}) => {


	
	return (
		<div className={[style.modal, style.active].join(' ')}>
			<div className={style.content}>
				{children}
			</div>
		</div>
	);
}

export default Modal;
