import { useMemo } from "react"
import { getPageCount } from "../../utils/pages"

export const usePagination=(totalCount, limit)=>{
	const totalPages = useMemo(()=>{
		return getPageCount(totalCount, limit)
	}, [totalCount, limit])
	return totalPages
}
