import React,{useState} from 'react';
import AddForm from './Components/AddForm/AddForm';
import PostList from './Components/PostList/PostList';
import './styles/App.css';
import CustomSelect from './Components/UI/select/CustomSelect';
import CustomInput from './Components/UI/Input/CustomInput';

function App() {

	const [posts,setPosts]=useState([
	{id:1,title:"aa-1",body:"3escription"},
	{id:2,title:"vv-2",body:"6escription"},
	{id:3,title:"cc-3",body:"1escription"}
	])
	const [selSort,setSelSort]=useState('')
	const [searchString,setSearchString]=useState('')
	const sortedPosts=getSortedPosts()
	const createPost=(newPost)=>{
		setPosts([...posts,newPost])
	}
	const getSortedPosts=()=>{
		if(selSort)
			 return [...posts].sort((a,b)=>a[selSort].localeCompare(b[selSort]))
		else return posts
	}
	const removePost=(post)=>{
		setPosts(posts.filter(p=>p.id!==post.id))
	}
	const sortPosts=(sort)=>{
		setSelSort(sort)
	}
	return(
		<div className='App'>
			<AddForm posts={posts} create={createPost}/>
			<hr style={{margin:"10px 0 10px 0", width:"80%"}}/>

			<CustomInput
				placeholder="Поиск"
				value={searchString}
				onChange={(e)=>setSearchString(e.target.value)}
			/>

			<CustomSelect 
				defVal={"Сортировка по"}
				value={selSort}
				onChange={sortPosts}
				options={[
					{value:"title",name:"По названию"},
					{value:"body",name:"По содержанию"}
				]}
			/>
			{
				posts.length!==0
				?<PostList title={"Список постов про JS"} posts={sortedPosts} create={createPost} remove={removePost}/>
				: <h1>Постов нет</h1>
			}

		</div>

	)
}

export default App;
