import './App.css';
import React,{useState} from 'react';

function App() {
	const [sentence, setSentence] = useState("dfvdsfv")
	return(
		<div><input type="text" onChange = {(e)=>setSentence(e.target.value)}/>
		<h1 >{sentence}</h1></div>

	)
}

export default App;
