import React from 'react';
import AddForm from '../AddForm/AddForm';
import PostItem from './../Posts/PostItem';

const PostList = ({posts,add,list}) => {
	
	return(
		<div className='App'>
			<AddForm posts={posts} add={add}/>
			<h1>Список постов {list}</h1>
			{
				posts.map(post=> <PostItem post={post} key={post.id} posts={posts} update={add}/>)
			}
		</div>
	)
}

export default PostList;
