import React,{useState} from 'react';
import AddForm from './Components/AddForm/AddForm';
import PostList from './Components/PostList/PostList';
import './styles/App.css';
import CustomSelect from './Components/UI/select/CustomSelect';

function App() {

	const [posts,setPosts]=useState([
	{id:1,title:"JS-1",body:"description"},
	{id:2,title:"JS-2",body:"description"},
	{id:3,title:"JS-3",body:"description"}
	])
	const [selSort,setSelSort]=useState('')
	const createPost=(newPost)=>{
		setPosts([...posts,newPost])
	}
	const removePost=(post)=>{
		setPosts(posts.filter(p=>p.id!==post.id))
	}
	const sortPosts=(sort)=>{
		setSelSort(sort)
		setPosts([])
	}
	return(
		<div className='App'>
			<AddForm posts={posts} create={createPost}/>
			<hr style={{margin:"10px 0 10px 0", width:"80%"}}/>
			<CustomSelect 
				defVal={"Сортировка по"}
				value={selSort}
				onChange={sortPosts}
				options={[
					{value:"title",name:"По названию"},
					{value:"body",name:"По содержанию"}
				]}
			/>
			{
				posts.length!==0
				?<PostList title={"Список постов про JS"} posts={posts} create={createPost} remove={removePost}/>
				: <h1>Постов нет</h1>
			}

		</div>

	)
}

export default App;
