import './App.css';
import React,{useState} from 'react';
import NewForm from './Components/AddForm/NewForm';

function App() {
	const [sentence, setSentence] = useState(['text1','text2',])
	const UpdateArr=(text)=>{

		setSentence([...sentence,text])
		console.log(sentence)
	}
	return(
		<div className='main'>

			<h1 style={{fontSize:"4em"}}>{sentence}</h1>
			<NewForm update={UpdateArr}/>
		</div>
	)
}

export default App;
