import logo from './logo.svg';
import './App.css';
import React,{useState} from 'react';

function App() {
	const [counter,setCounter]=useState("");
  return (
    <div className="App">
		<input onChange={()=>{setCounter(this.text)}}/>
		<br/>
		<h1 className="Text">{counter}</h1>
	</div>
  );
}

export default App;
