import React,{useState} from 'react';
import PostItem from './../Posts/PostItem';

const PostList = ({posts,add}) => {

	return(
		<div className='App'>
			{
				posts.map(post=> <PostItem post={post} key={post.id} posts={posts} update={add}/>)
			}
		</div>
	)
}

export default PostList;
