		add(...posts,{id:Date.now(),title:title,body:body})
	}
	return (
		<form>
			<CustomInput 
				type="text"
				placeholder='Title'
				onChange={e=>setTitle(e.target.value)}
			/>
			<CustomInput 
				type="text"
				placeholder='Body'
				onChange={e=>setBody(e.target.value)}
			/>
			<CustomButton onClick={AddForm}>Добавить</CustomButton>
		</form>
	);
}

export default AddForm;
